import { Component, OnInit, Inject, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';
import { DropdownUsersService } from './dropdown-users.service';
import { FormGroup, FormsModule, ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-dropdown-users',
  templateUrl: './dropdown-users.component.html',
  styleUrls: ['./dropdown-users.component.css']
})
export class DropdownUsersComponent implements OnInit {
  public l_users: User[] = [];
  userObservable$:any;

  constructor(private service:DropdownUsersService, @Inject('BASE_URL') baseUrl: string) {

    /*http.get<User[]>(baseUrl + 'user').subscribe(result => {
      this.l_users = result;
    }, error => console.error(error));*/
  }

  GetAll()  {
    this.service.GetAll().subscribe(result => {
      this.userObservable$=result;
    })
  }

  ngOnInit(): void {
    this.form = new FormGroup({
      town: new FormControl()
    });
    this.GetAll();
  }

}

interface Address
{
    street: string; 
    suite: string;
    city: string; 
    zipcode: string;
    geo: Geo;
}

interface Company
{
    name: string;
    catchPhrase: string;
    bs: string;
}

interface Geo
{
    lat: string;
    lng: string;
}

interface User
{
    id: number;
    name: string;
    username: string;
    email: string;
    address: Address;
    phone: string;
    website: string;
    company: Company;
}
